import { type } from 'os';
import React, { useEffect, useState } from 'react';



function ListaDeRepositorios() {
    const [repositorio, setRepositorio] = useState({
      id:Number,
      name:String,
});


    useEffect(() => {
        async function carregaRepositorios () {
            const resposta = await fetch('https://api.github.com/users/julio-cesar96/repos');
            const repositorio = await resposta.json();

            setRepositorio(repositorio);
          }
          carregaRepositorios();
        }, []);


  return (
    <>
        <ul>
        {repositorio.map((repositorio) =>(
          <li key={repositorio.id}>
            {repositorio.name}
          </li>
        ))}
      </ul>
      <button onClick={()=>console.log(repositorio)}>aqui</button>
   </>
  );
}
export default ListaDeRepositorios;
