import React, { useEffect, useState } from 'react';

 interface Props{
  id: string;
  name: string;
 }
function ListaDeRepositorios({id,name}:Props) {
    const [repositorio, setRepositorio] = useState({
      id:"",
      name:"",

    });
    useEffect(() => {
        async function carregaRepositorios () {
            const resposta = await fetch('https://api.github.com/users/julio-cesar96/repos');
            const repositorio = await resposta.json();

            setRepositorio(repositorio);
          }
          carregaRepositorios();
        }, []);


  return (
    <ul>
    <li>Curso da Alura - HTML e CSS</li>
    <li>Curso da Alura - React</li>
    <li>Curso da Alura - Manipulando DOM com JS</li>
   </ul>
  );
}
